import React from 'react';
import { connect } from 'react-redux';
import { signIn, signOut } from '../actions';

class GoogleAuth extends React.Component {

    state = { isSignedIn: null };

componentDidMount() {


    window.gapi.load("client:auth2",() => {
        window.gapi.client.init({
            clientId:'706023751751-ip685a9hpcduj3h3pr0pbbb6rr19bih6.apps.googleusercontent.com',
            scope: 'email'
        }).then(() => {
            this.auth = window.gapi.auth2.getAuthInstance();
            this.setState({ isSignedIn: this.auth.isSignedIn.get() });
            this.auth.isSignedIn.listen(this.onAuthChange);
        });
    });
}

onAuthChange = () => {
    this.setState({ isSignedIn: this.auth.isSignedIn.get() });
};

onSignInClick = () => {
    this.auth.signIn();
};
 
onSignOutClick = () => {
    this.auth.signOut();
};


renderAuthButton(){
 if(this.state.isSignedIn === null){
     return null;
 } else if (this.state.isSignedIn === true){
     return(
         <button onClick={this.onSignOutClick} className="btn btn-outline-success">
         <i className="fab fa-google" style={{marginRight:5}} />
         Sign Out
         </button>
     );
 }else{
     return(
        <button onClick={this.onSignInClick} className="btn btn-outline-success">
         <i className="fab fa-google" style={{marginRight:5}} />
         Sign Ä°n
         </button>
     );
 }
}

    render(){
        return(<div>{this.renderAuthButton()}</div>);
    }
}

export default connect(
    null,
    
) (GoogleAuth);
