import React from 'react';
import {Link} from 'react-router-dom';


class Header  extends React.Component {

  state = { isSignedIn: null };

  componentDidMount() {
    window.gapi.load('client:auth2', () => {
        window.gapi.client.init({
            clientId:'591677932235-murc9nf1avmlbika22ghs44a8n7ff08t.apps.googleusercontent.com',
            scope: 'email'
        }).then(() => {
            this.auth = window.gapi.auth2.getAuthInstance();
            this.setState({ isSignedIn: this.auth.isSignedIn.get() });
            this.auth.isSignedIn.listen(this.onAuthChange);
        });
    });
}

onAuthChange = () => {
  this.setState({ isSignedIn: this.auth.isSignedIn.get( ) });
};

renderAuthButton(){
if(this.state.isSignedIn === null){
   return <div>I dont know if we are signed in</div>;
} else if (this.state.isSignedIn === true){
   return <div>I am signed in !</div>;
}else{
   return <div>I am not signed in</div>;
}
}

     render(){
       return(
<nav className="navbar navbar-expand-lg fixed-top navbar-light bg-light">
      <Link className="navbar-brand" to="/">Streamy</Link>
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarColor01" aria-controls="navbarColor01" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
      </button>
    
      <div className="collapse navbar-collapse" id="navbarColor01">
        <ul className="navbar-nav mr-auto">
          <li className="nav-item active">
            <Link className="nav-link" to="/">Home <span className="sr-only">(current)</span></Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="/page">Features</Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="#">Pricing</Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="#">About</Link>
          </li>
         
        </ul>
        
        <form className="form-inline my-2 my-lg-0">
          <Link className="nav-link" to="#">All Streams</Link>
          {this.renderAuthButton}
          </form>
       
      
      </div>
    </nav>
       );
     }
      
};

export default Header;
