import React from 'react';
import { connect } from 'react-redux'
import { fetchStreams } from '../../actions'
import  './style.css';

class StreamList extends React.Component {

    componentDidMount(){
        this.props.fetchStreams()
    }

    renderAdmin(stream){
        if(stream.userId === this.props.currentUserId){
            return (
                <div style={{marginLeft:50}}>
                     <li className="breadcrumb-item active">
                     <button style={{marginRight:5}} type="button" className="btn btn-outline-info">Edit</button>
                     <button type="button" className="btn btn-outline-danger">Delete</button>
                     </li>
                </div>
            );
        }
    }
   

    renderList(){
        return this.props.streams.map(stream => {
            return(
                <div className="row"  key={stream.id}> 
                <ol className="breadcrumb" style={{items}}>
                <li className="breadcrumb-item"><h4>{stream.title}</h4></li>
                <li className="breadcrumb-item"><p className="mb-0">{stream.description}</p></li>
                {this.renderAdmin(stream)}
                </ol>
                </div>
            );
        });
    }

    render(){
          return  (
         <div className="container">
         <div className="row">
         <h1>Streams</h1>
         </div>
        {this.renderList()}
         </div>
         
         
  
  );        
    }

}

const mapStateToProps = (state) => {
    return {
        streams: Object.values(state.streams),
        currentUserId: state.auth.userId
    };
};

export default connect(
    mapStateToProps,
    { fetchStreams }
    )(StreamList);
