import React from 'react';
import { Field, reduxForm } from 'redux-form';

class StreamCreate extends React.Component{
    renderInput({ input, label }) {
  return (
      <div>
      <label className="col-form-label" style={{paddingRight:5}}>{label} </label>
      <input className="form-control" style={{width:200}} {...input}  />
      </div>
  
   );
}

onSubmit(formValues){
    console.log(formValues);
}

    render(){
        return(
        <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="form-group" style={{display: 'flex', justifyContent:'center'}}>
            <fieldset>
            <Field name="title" component={this.renderInput} label="Enter Title " />
            <Field name="description" component={this.renderInput} label="Description " />
            <button style={{paddingTop:5}} className="btn btn-primary">Submit</button>
            </fieldset>
        </form>
        );
    
    }
};

export default reduxForm({
    form: 'streamCreate'
})(StreamCreate);
